mapper.select:
SELECT uto.trade_id AS tradeId, uto.type, uto.serial_no AS serialNo, uto.account_id AS accountId1, uto.fee AS fee1, uto.state, uto.description, utp.channel_id AS channelId, utp.account_id AS accountId2, utp.amount, utp.fee AS fee2, utp.created_time AS payTime, urp.amount AS refundAmount, urp.created_time AS refundTime FROM upay_trade_order uto INNER JOIN upay_trade_payment utp ON uto.trade_id = utp.trade_id LEFT JOIN upay_refund_payment urp ON uto.trade_id = urp.trade_id AND uto.state IN (5, 6) WHERE uto.state IN (4, 5, 6) AND (uto.account_id = #{accountId} OR utp.account_id = #{accountId})
AND utp.created_time > #{startDate}
AND utp.created_time < #{endDate}
ORDER BY utp.created_time DESC LIMIT #{start}, #{limit}

SELECT COUNT(*) FROM upay_trade_order uto INNER JOIN upay_trade_payment utp ON uto.trade_id = utp.trade_id LEFT JOIN upay_refund_payment urp ON uto.trade_id = urp.trade_id AND uto.state IN (5, 6) WHERE uto.state IN (4, 5, 6) AND (uto.account_id = #{accountId} OR utp.account_id = #{accountId})
AND utp.created_time > #{startDate}
AND utp.created_time < #{endDate}

SELECT
    SUM(CASE WHEN action = 1 THEN amount ELSE 0 END) AS income, SUM(CASE WHEN action = 2 THEN -amount ELSE 0 END) AS output
FROM
    upay_fund_statement
WHERE
    account_id = #{accountId}

        AND created_time > #{startDate}


        AND created_time < #{endDate}

GROUP BY account_id
